import React, { useState } from "react";
import { View, Text, TextInput, FlatList, TouchableOpacity, Image, StyleSheet, Alert } from "react-native";
import { useNavigation } from "@react-navigation/native";

// Importing local images
import foodPalace from "../assets/images/foodPalace.jpeg";
import tastyBites from "../assets/images/tastybites.jpeg";


const restaurants = [
    {
        id: "1",
        name: "Tasty Bites",
        image: tastyBites,
        dishes: [
            { id: "101", name: "Veggie Tomato Mix", price: "₦1,900", image: "data:image/jpeg;base64," },
            { id: "102", name: "Spicy Chicken", price: "₦2,300", image: "data:image/jpeg;base64," },
        ],
    },
    {
        id: "2",
        name: "Food Palace",
        image: foodPalace,
        dishes: [
            { id: "201", name: "Beef Stew", price: "₦2,500", image: "data:image/jpeg;base64," },
            { id: "202", name: "Fried Rice", price: "₦1,800", image: "data:image/jpeg;base64," },
        ],
    },
];

const HomeScreen = () => {
    const navigation = useNavigation();
    const [searchText, setSearchText] = useState("");
    const [filteredDishes, setFilteredDishes] = useState([]);

    const handleSearch = (text) => {
        setSearchText(text);
        if (text.length > 0) {
            const matches = [];
            restaurants.forEach((restaurant) => {
                restaurant.dishes.forEach((dish) => {
                    if (dish.name.toLowerCase().includes(text.toLowerCase())) {
                        matches.push({ ...dish, restaurantName: restaurant.name });
                    }
                });
            });

            if (matches.length === 0) {
                Alert.alert("No Results", "No matching dishes found.");
            }

            setFilteredDishes(matches);
        } else {
            setFilteredDishes([]);
        }
    };

    return (
        <View style={styles.container}>
            <Text style={styles.header}>Home</Text>

            {/* Search Bar */}
            <TextInput
                style={styles.searchBar}
                placeholder="Search for a dish..."
                value={searchText}
                onChangeText={handleSearch}
            />

            {/* Search Results */}
            {filteredDishes.length > 0 ? (
                <FlatList
                    data={filteredDishes}
                    keyExtractor={(item) => item.id}
                    renderItem={({ item }) => (
                        <TouchableOpacity style={styles.dishItem}>
                            <Image source={item.image} style={styles.dishImage} />
                            <View>
                                <Text style={styles.dishName}>{item.name}</Text>
                                <Text style={styles.restaurantName}>{item.restaurantName}</Text>
                                <Text style={styles.dishPrice}>{item.price}</Text>
                            </View>
                        </TouchableOpacity>
                    )}
                />
            ) : (
                <FlatList
                    data={restaurants}
                    keyExtractor={(item) => item.id}
                    renderItem={({ item }) => (
                        <TouchableOpacity
                            style={styles.restaurantCard}
                            onPress={() => navigation.navigate("RestaurantScreen", { restaurant: item })}
                        >
                            <Image source={item.image} style={styles.restaurantImage} />
                            <Text style={styles.restaurantName}>{item.name}</Text>
                        </TouchableOpacity>
                    )}
                />
            )}
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 15,
        backgroundColor: "#F2F2F2",
    },
    header: {
        fontSize: 24,
        fontWeight: "bold",
        textAlign: "center",
        marginBottom: 15,
        paddingTop: 30,
    },
    searchBar: {
        backgroundColor: "#fff",
        padding: 10,
        borderRadius: 10,
        marginBottom: 10,
        fontSize: 16,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 1 },
        shadowOpacity: 0.2,
        shadowRadius: 2,
    },
    restaurantCard: {
        backgroundColor: "#fff",
        marginBottom: 10,
        padding: 15,
        borderRadius: 10,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.1,
        shadowRadius: 4,
    },
    restaurantImage: {
        width: "100%",
        height: 120,
        borderRadius: 10,
        marginBottom: 10,
    },
    restaurantName: {
        fontSize: 18,
        fontWeight: "bold",
    },
    dishItem: {
        flexDirection: "row",
        backgroundColor: "#fff",
        marginBottom: 10,
        padding: 10,
        borderRadius: 10,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 1 },
        shadowOpacity: 0.1,
        shadowRadius: 2,
    },
    dishImage: {
        width: 60,
        height: 60,
        borderRadius: 10,
        marginRight: 10,
    },
    dishName: {
        fontSize: 16,
        fontWeight: "bold",
    },
    dishPrice: {
        color: "green",
        fontSize: 14,
    },
});

export default HomeScreen;
